import React from "react";
import ExButton from "../images/close_icon.svg";
import PopupWithForm from "./PopupWithForm";

const addPopup = new PopupWithForm({classSelector: ".popup_add", submitFunc: handleAddFormSubmit});

function handleAddFormSubmit(evt) {
    // evt.preventDefault();
	console.log(evt);
    // const inVal = addPopup.getInputValues();
    // api.addCard(inVal.title, inVal.imagelink)
    //     .then((data) => {
    //         cardsSection.addItem({
    //             name: inVal.title,
    //             link: inVal.imagelink,
    //             likes: [],
    //             owner: userInfo.getMe(),
    //             id: data._id
    //         });
    //         toggleAddPopupWindow();
    //     })
    //     .finally(() => {
    //         addPopup.querySelector(".popup__button").textContent = "Save";
    //     }).catch((err) => {
    //         console.log(err);
    //     });
}

function Main(){
	const toggleAvatarPopupWindow =()=> {
		const avatarPopup = document.querySelector(".popup_avatar");
		if (!avatarPopup.classList.contains("popup_opened")) {
			avatarPopup.classList.add("popup_opened");
		} else {
			avatarPopup.classList.remove("popup_opened");
		}
	}

	const toggleEditPopupWindow =()=> {
		const editPopup = document.querySelector(".popup_profile");
		if (!editPopup.classList.contains("popup_opened")) {
			editPopup.classList.add("popup_opened");
		} else {
			editPopup.classList.remove("popup_opened");
		}
	}

	const toggleAddPopupWindow =()=> {
		if (!addPopup.checkIfOpened()) {
			addPopup.open();
		} else {
			addPopup.close();
		}
	}

	return(<>
		<main class="main">
                <section className="profile">
                    <img className="profile__avatar" src=" " />
                    <button className="profile__image-button" type="button" onClick={toggleAvatarPopupWindow}></button>
                    <div className="profile__info">
                        <div className="profile__container">
                            <h1 className="profile__name text-hiding" id="profilename"> </h1>
                            <button className="profile__edit-button" type="button" onClick={toggleEditPopupWindow}></button>
                        </div>
                        <p id="profiledescription" className="profile__about-me text-hiding"> </p>
                    </div>
                    <button className="profile__add-button" onClick={toggleAddPopupWindow}>+</button>
                </section>

                <section className="cards">
                </section>

                <div className="popup popup_profile">
                    <div className="popup__content">
                        <button className="popup__close-button" id="editCloseButton" type="button" onClick={toggleEditPopupWindow}><img src={ExButton} alt="ex" /></button>
                        <h2 className="popup__title">Edit profile</h2>
                        <form className="popup__form" id="editform" name="editform" novalidate>
                            <input className="popup__input" required type="text" name="name" minlength="2" maxlength="40" id="popupname" placeholder="Jacques Cousteau" />
                            <h4 className="popup__error-massage popupname-error">Please fill out this field.</h4>
                            <input className="popup__input" required type="text" name="description" minlength="2" maxlength="200" id="popupaboutme" placeholder="Explorer" />
                            <h4 className="popup__error-massage popupaboutme-error">Please fill out this field.</h4>
                            <button className="popup__button" id="savebutton" type="submit">Save</button>
                        </form>
                    </div>
                </div>

                <div className="popup popup_add">
                    <div className="popup__content">
                        <button className="popup__close-button" id="addCloseButton" type="button" onClick={toggleAddPopupWindow}><img src={ExButton} alt="ex" /></button>
                        <h2 className="popup__title">New place</h2>
                        <form className="popup__form" id="addform" name="addform" novalidate>
                            <input className="popup__input" required type="text" minlength="1" maxlength="30" name="title" id="title" placeholder="Title" />
                            <h4 className="popup__error-massage title-error">Please fill out this field.</h4>
                            <input className="popup__input" required type="url" name="imagelink" id="imagelink" placeholder="Image link" />
                            <h4 className="popup__error-massage imagelink-error">Please enter a web address.</h4>
                            <button className="popup__button popup__button_invalid" id="createbutton" type="submit" disabled>Save</button>
                        </form>
                    </div>
                </div>

                <div className="popup popup_avatar">
                    <div className="popup__content">
                        <button className="popup__close-button" id="avatarCloseButton" type="button"><img src={ExButton} alt="ex" onClick={toggleAvatarPopupWindow} /></button>
                        <h2 className="popup__title">Change profile picture</h2>
                        <form className="popup__form" id="avatarform" name="avatarform" novalidate>
                            <input className="popup__input" required type="url" minlength="1" maxlength="256" name="avatar" id="avatar" placeholder="Enter picture URL" />
                            <h4 className="popup__error-massage avatar-error">Please fill out this field.</h4>
                            <button className="popup__button popup__button_invalid" id="avatarbutton" type="submit" disabled>Save</button>
                        </form>
                    </div>
                </div>

                <div className="popup popup_confirm">
                    <div className="popup__content">
                        <button className="popup__close-button" id="confirmCloseButton" type="button"><img src="<%=require('./images/close_icon.svg')%>" alt="ex" /></button>
                        <h2 className="popup__title">Are you sure?</h2>
                        <button className="popup__button">Yes</button>
                    </div>
                </div>

                <div className="popup popup_image">
                    <div className="popup__image-container">
                        <button className="popup__close-button popup__close-button_image" id="imageCloseButton" type="button"><img src="<%=require('./images/close_icon.svg')%>" alt="ex" /></button>
                        <img className="popup__image" src="#" alt=" " />
                        <h2 className="popup__text"> </h2>
                    </div>
                </div>
            </main>
	</>);
}

export default Main;
