import Popup from "./Popup";
import Ex from '../images/close_icon.svg'

export default class PopupWithForm extends Popup {
    constructor(props) {
        super(props.classSelector);
        this._submit = props.submit;
        this._form = this._element.querySelector(".popup__form")
        this._inputs = this._element.querySelectorAll(".popup__input");
        this._button = this._element.querySelector(".popup__button");
    }

    getInputValues() {
        const formValues = {};
        for (let i = 0; i < this._inputs.length; i++) {
            formValues[this._inputs[i].name] = this._inputs[i].value;
        }

        return formValues;
    }

    close() {
        this._form.reset();
        super.close();
    }

    _handleSubmit(evt){
        this._button.textContent = "Saving...";
        this._submit(evt);
    }

    render(){
        <div className="popup popup_profile">
            <div className="popup__content">
                <button className="popup__close-button" id="editCloseButton" type="button"><img src={ExButton} alt="ex" /></button>
                <h2 className="popup__title">Edit profile</h2>
                <form className="popup__form" onSubmit={this._handleSubmit} id="editform" name="editform" novalidate>
                    <input className="popup__input" required type="text" name="name" minlength="2" maxlength="40" id="popupname" placeholder="Jacques Cousteau" />
                    <h4 className="popup__error-massage popupname-error">Please fill out this field.</h4>
                    <input className="popup__input" required type="text" name="description" minlength="2" maxlength="200" id="popupaboutme" placeholder="Explorer" />
                    <h4 className="popup__error-massage popupaboutme-error">Please fill out this field.</h4>
                    <button className="popup__button" id="savebutton" type="submit">Save</button>
                </form>
            </div>
        </div>
    }
}
